const { execSync } = require('child_process');
const path = require('path');

const workingDir = 'C:\\Users\\yyutingliu\\Downloads\\AI生成網站測試\\cursor\\一個主題多個站(落地)';
const scriptPath = path.join(workingDir, 'ai-generate-articles.cjs');

// 設定環境變數
const env = {
  ...process.env,
  SITES: $input.item.json.SITES,
  DATE: $input.item.json.DATE,
  COUNT: $input.item.json.COUNT,
  CATEGORY: $input.item.json.CATEGORY,
  STRAPI_URL: $input.item.json.STRAPI_URL,
  STRAPI_TOKEN: $input.item.json.STRAPI_TOKEN,
  GEMINI_API_KEY: $input.item.json.GEMINI_API_KEY,
  PROMPT_FILE: $input.item.json.PROMPT_FILE,
  OUTPUT_JSON: $input.item.json.OUTPUT_JSON,
  GITHUB_REPO_PATH: $input.item.json.GITHUB_REPO_PATH,
  GITHUB_AUTO_PUSH: $input.item.json.GITHUB_AUTO_PUSH
};

try {
  const output = execSync(`node "${scriptPath}"`, {
    cwd: workingDir,
    env: env,
    encoding: 'utf8'
  });

  return {
    json: {
      exitCode: 0,
      output: output,
      success: true
    }
  };
} catch (error) {
  return {
    json: {
      exitCode: error.status || 1,
      output: error.stdout || error.message,
      error: error.message,
      success: false
    }
  };
}

const { execSync } = require('child_process');
const path = require('path');

const workingDir = 'C:\\Users\\yyutingliu\\Downloads\\AI生成網站測試\\cursor\\一個主題多個站(落地)';
const scriptPath = path.join(workingDir, 'export-strapi-to-github.cjs');

// 從 Set Variables 節點取得資料
const setVarsNode = $('Set Variables').first();

// 設定環境變數
const env = {
  ...process.env,
  SITES: setVarsNode.json.SITES,
  DATE: setVarsNode.json.DATE,
  CATEGORY: setVarsNode.json.CATEGORY,
  STRAPI_URL: setVarsNode.json.STRAPI_URL,
  STRAPI_TOKEN: setVarsNode.json.STRAPI_TOKEN,
  GITHUB_REPO_PATH: setVarsNode.json.GITHUB_REPO_PATH,
  GITHUB_REPO_URL: setVarsNode.json.GITHUB_REPO_URL,
  GITHUB_AUTO_PUSH: setVarsNode.json.GITHUB_AUTO_PUSH,
  OUTPUT_JSON: 'true'
};

try {
  const output = execSync(`node "${scriptPath}"`, {
    cwd: workingDir,
    env: env,
    encoding: 'utf8'
  });

  return {
    json: {
      exitCode: 0,
      output: output,
      success: true
    }
  };
} catch (error) {
  return {
    json: {
      exitCode: error.status || 1,
      output: error.stdout || error.message,
      error: error.message,
      success: false
    }
  };
}
