const { execSync } = require('child_process');
const path = require('path');

const workingDir = 'C:\\Users\\yyutingliu\\Downloads\\AI生成網站測試\\cursor\\一個主題多個站(落地)';
const scriptPath = path.join(workingDir, 'ai-generate-articles.cjs');

// 從 Set Variables 節點取得資料
const inputData = $input.item.json;

// 處理數量（如果是範圍，隨機選擇）
let finalCount = inputData.COUNT;
if (typeof finalCount === 'string' && finalCount.includes('-')) {
  const [min, max] = finalCount.split('-').map(Number);
  finalCount = Math.floor(Math.random() * (max - min + 1)) + min;
  console.log(`隨機數量: ${min}-${max} → 選擇 ${finalCount}`);
}

// 設定環境變數
const env = {
  ...process.env,
  SITES: inputData.SITES,
  DATE: inputData.DATE,
  COUNT: finalCount.toString(),
  CATEGORY: inputData.CATEGORY,
  STRAPI_URL: inputData.STRAPI_URL,
  STRAPI_TOKEN: inputData.STRAPI_TOKEN,
  GEMINI_API_KEY: inputData.GEMINI_API_KEY,
  PROMPT_FILE: inputData.PROMPT_FILE,
  OUTPUT_JSON: inputData.OUTPUT_JSON,
  GITHUB_REPO_PATH: inputData.GITHUB_REPO_PATH,
  GITHUB_AUTO_PUSH: inputData.GITHUB_AUTO_PUSH
};

try {
  console.log(`執行腳本: ${scriptPath}`);
  console.log(`參數: SITES=${inputData.SITES}, DATE=${inputData.DATE}, COUNT=${finalCount}, CATEGORY=${inputData.CATEGORY}`);

  const output = execSync(`node "${scriptPath}"`, {
    cwd: workingDir,
    env: env,
    encoding: 'utf8',
    maxBuffer: 10 * 1024 * 1024 // 10MB buffer
  });

  // 嘗試從輸出中解析 JSON 結果
  let result = {
    exitCode: 0,
    output: output,
    success: true
  };

  // 如果輸出包含 JSON，嘗試解析
  const jsonMatch = output.match(/\{[\s\S]*"success"[\s\S]*\}/);
  if (jsonMatch) {
    try {
      const parsed = JSON.parse(jsonMatch[0]);
      result = { ...result, ...parsed };
    } catch (e) {
      // JSON 解析失敗，使用原始輸出
    }
  }

  // 從輸出中提取統計資訊
  const successMatch = output.match(/✅ 成功: (\d+)/);
  const failMatch = output.match(/❌ 失敗: (\d+)/);

  if (successMatch) {
    result.successCount = parseInt(successMatch[1]);
  }
  if (failMatch) {
    result.failCount = parseInt(failMatch[1]);
  }

  return {
    json: result
  };
} catch (error) {
  console.error('執行錯誤:', error);
  return {
    json: {
      exitCode: error.status || 1,
      output: error.stdout || error.message,
      error: error.message,
      stderr: error.stderr,
      success: false
    }
  };
}

